import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex font-barlow items-center justify-center whitespace-nowrap transition-all duration-150 ease-linear rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-dark text-dark-foreground hover:bg-primary/90",
        primary: "bg-transparent rounded-md text-background hover:text-dark pe-[5rem] bg-contain bg-no-repeat bg-center bg-[url(data:image/svg+xml;base64,)] dark:bg-[url(data:image/svg+xml;base64,)] hover:bg-[url(data:image/svg+xml;base64,)] dark:hover:bg-[url(data:image/svg+xml;base64,)] !h-[3.25rem] text-md font-medium rounded-md py-3 min-w-[13rem] px-6 relative [&_.icon]:absolute [&_.icon]:-translate-y-2/4 [&_.icon]:end-[1.15rem] [&_.icon]:top-2/4",
        outlinePrimary: "bg-transparent rounded-md text-dark hover:text-dark-foreground pe-[5rem] bg-contain bg-no-repeat bg-center bg-[url(data:image/svg+xml;base64,)] dark:bg-[url(data:image/svg+xml;base64,)] hover:bg-[url(data:image/svg+xml;base64,)] dark:hover:bg-[url(data:image/svg+xml;base64,)] !h-[3.25rem] text-md font-medium rounded-md py-3 min-w-[13rem] px-6 relative [&_.icon]:absolute [&_.icon]:-translate-y-2/4 [&_.icon]:end-[1.15rem] [&_.icon]:top-2/4",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-black bg-background text-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-12 rounded-md px-6",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  }
)
Button.displayName = "Button"

export { Button, buttonVariants }
